   private int numThreads = 1;
   /** Creates new form SliceUI */
   public GUI()
   {
      numThreads = VisNow.availableProcessors();
      initComponents();
      dimSlider.setVal(50);
   }
   
   public int getResolution()
   {
      int r = dimSlider.getVal();
      if (r<3) return 3;
      return r;
   }

   public int getNThreads()
   {
      return numThreads;
   }
   
